var link=document.querySelector(".btn-contacts"),popup=document.querySelector(".modal-content"),close=popup.querySelector(".modal-content-close"),form=popup.querySelector("form"),login=popup.querySelector("[name=login]"),email=popup.querySelector("[name=email]"),storage=localStorage.getItem("login");link.addEventListener("click",function(a){a.preventDefault(),popup.classList.add("modal-content-show"),storage?(login.value=storage,email.focus()):login.focus()}),close.addEventListener("click",function(a){a.preventDefault(),popup.classList.remove("modal-content-show")}),form.addEventListener("submit",function(a){login.value&&email.value?localStorage.setItem("login",login.value):(a.preventDefault(),console.log("Нужно ввести логин и e-mail"))}),window.addEventListener("keydown",function(a){27===a.keyCode&&popup.classList.contains("modal-content-show")&&popup.classList.remove("modal-content-show")});